lien_markdown &lt;- function(nom, lien) { 
  return(glue::glue("[{nom}]({lien})"))
}

lien_html &lt;- function(nom, lien) {
  return(glue::glue('&lt;a href="{lien}"&gt;{nom}&lt;/a&gt;'))
}

cran_repo &lt;- "https://cran.r-project.org/web/packages/"

# last_pkg_puplished added with markdown links to PostgreSQL database
last_pkg_published &lt;- pdb |&gt; 
  select(Package, Version, `Date/Publication`) |&gt; 
  filter(
    date(ymd_hms(`Date/Publication`)) == (Sys.Date() - 1)
  ) |&gt; 
  arrange(desc(`Date/Publication`), Package, Version) |&gt;
  rename(
    paquet = Package,
    version = Version,
    date_publication = `Date/Publication`
  ) |&gt; 
  mutate(lien = lien_markdown(paquet, paste0(cran_repo, paquet))) |&gt; 
  distinct(.keep_all = TRUE)

# last_pkg_puplished_DT used to display table with DT::datatable
last_pkg_published_DT &lt;- pdb |&gt; 
  select(Package, Version, `Date/Publication`) |&gt; 
  filter(
    date(ymd_hms(`Date/Publication`)) == (Sys.Date() - 1)
  ) |&gt; 
  arrange(desc(`Date/Publication`), Package, Version) |&gt;
  rename(
    paquet = Package,
    version = Version,
    date_publication = `Date/Publication`
  ) |&gt; 
  mutate(paquet = lien_html(paquet, paste0(cran_repo, paquet))) |&gt; 
